INSERT INTO dict_table (database_id, table_id, table_desc, tbl_comment, audit_logging, default_orderby, created_date, created_user, revised_date, revised_user) VALUES('menu', 'mnu_control', 'Control', 'This holds control parameters for the system. Although they are held on the database as separate records, each with a NAME and a VALUE, the online screen treats them as separate fields within the same record.\r\n\r\nThis allows fields to be added to or removed from the online screen without having to change the structure of the table.', 'Y', 'record_id, field_id', '2005-04-07 11:16:02', 'AJM', '2007-02-24 15:44:53', 'AJM');
