INSERT INTO dict_table (database_id, table_id, table_desc, tbl_comment, audit_logging, default_orderby, created_date, created_user, revised_date, revised_user) VALUES('menu', 'mnu_subsystem', 'Subsystem', 'This breaks down an application into logical groups, such as MENU, AUDIT, DICTIONARY and WORKFLOW. All the files for each subsystem can be maintained in a separate subdirectory, and the contents of the menu data base for each subsystem can be exported individually so that the can also be imported indvidually.', 'Y', 'subsys_id', '2005-04-07 11:16:02', 'AJM', '2007-02-24 15:45:38', 'AJM');
