REPLACE INTO `mnu_pattern` VALUES('ADD1', 'ADD1 - Insert a single record', 'This will allow the user to add a single record to a database table.\r\n\r\nThis will call the ''insertRecord'' method just once.', 'Y', 'N', 'N', '2003-01-01 12:00:00', 'AJM', '2004-04-11 16:38:46', 'AJM');
REPLACE INTO `mnu_pattern` VALUES('ADD2', 'ADD2 - Insert a single record', 'This will add an occurrence to the MANY entity in a ONE-to-MANY relationship with the primary key of the ONE entity being passed down from the previous screen.\r\n\r\nThis will call the ''insertRecord'' method just once.', 'Y', 'Y', 'N', '2003-01-01 12:00:00', 'AJM', '2004-04-11 16:38:59', 'AJM');
REPLACE INTO `mnu_pattern` VALUES('ADD3', 'ADD3 - Insert multiple records without user dialog', 'This will add records to the database without any dialog with the user.\r\n\r\nIt will use the primary key supplied by the previous screen and one or more primary keys selected in a popup screen which will be called next.\r\n\r\nThis uses repeated calls to the ''insertRecord'' method.', 'N', 'N', 'N', '2003-01-01 12:00:00', 'AJM', '2004-04-11 16:39:10', 'AJM');
REPLACE INTO `mnu_pattern` VALUES('ADD4', 'ADD4 - Insert one or more records without user dialog', 'This will allow one or more database records to be created without any dialog with the user.\r\n\r\nDetails of the parent table are passed down in $where.\r\n\r\nThis will use the ''insertMultiple'' method.', 'N', 'N', 'N', '2003-01-01 12:00:00', 'AJM', '2004-05-10 22:33:14', 'AJM');
REPLACE INTO `mnu_pattern` VALUES('DEL1', 'DEL1 - Delete a single record', 'This will delete a single record from the database using the primary key passed down by the previous screen.\r\n\r\nThis will call the ''deleteRecord'' method.', 'Y', 'Y', 'N', '2003-01-01 12:00:00', 'AJM', '2004-04-11 16:39:45', 'AJM');
REPLACE INTO `mnu_pattern` VALUES('DEL2', 'DEL2 - Delete multiple records without user dialog', 'This will delete multiple records one at a time using the primary keys passed down from the previous screen.\r\n\r\nThis uses the ''deleteMultiple'' method.', 'N', 'Y', 'N', '2003-01-01 12:00:00', 'AJM', '2004-10-01 16:56:16', 'AJM');
REPLACE INTO `mnu_pattern` VALUES('DEL3', 'DEL3 - Delete multiple records without user dialog', 'This will call the ''deleteSelection'' method to delete/update a selection of records using the primary keys passed down from the previous screen.', 'N', 'Y', 'N', '2003-01-01 12:00:00', 'AJM', '2004-09-08 11:58:29', 'AJM');
REPLACE INTO `mnu_pattern` VALUES('DEL4', 'DEL4 - Delete parent and children', 'This will allow the user to delete a single record from the parent table, plus all related occurrences from the child table.\r\n\r\nThe primary key(s) for the occurrence(s) on the parent table is passed down by the previous screen.\r\n\r\nThis will call the ''deleteRecord'' method.', 'Y', 'Y', 'N', '2006-03-11 14:28:33', 'AJM', NULL, NULL);
REPLACE INTO `mnu_pattern` VALUES('ENQ1', 'ENQ1 - Enquire a single record', 'This will read a single record from the database using the primary key passed down by the previous screen and display it to the user.\r\n\r\nIf multiple rows are selected then scrolling options will be provided.', 'Y', 'Y', 'N', '2003-01-01 12:00:00', 'AJM', '2004-04-11 16:56:48', 'AJM');
REPLACE INTO `mnu_pattern` VALUES('ERASE1', 'ERASE1 - Delete a record and all child records', 'This will delete a single record from the database using the primary key passed down by the previous screen. It will also delete *ALL* related occurrences on child tables.\r\n\r\nThis will call the ''eraseRecord'' method.', 'Y', 'Y', 'N', '2005-12-06 17:40:51', 'AJM', NULL, NULL);
REPLACE INTO `mnu_pattern` VALUES('FILEDOWNLOAD', 'FILEDOWNLOAD - File Download', 'This will allow the user to download a file from the server to the client. The user selects an entity which contains the name of the file to be downloaded, then activates this transaction by pressing a navigation button.', 'N', 'Y', 'N', '2006-07-26 17:01:40', 'AJM', NULL, NULL);
REPLACE INTO `mnu_pattern` VALUES('FILEPICKER', 'FILEPICKER - File Picker', 'This will allow the user to select the name of a file.', 'Y', 'N', 'N', '2003-01-01 12:00:00', 'AJM', '2004-04-11 16:40:44', 'AJM');
REPLACE INTO `mnu_pattern` VALUES('FILEUPLOAD', 'FILEUPLOAD - File Upload', 'This will allow the user to upload a file from the client to the server. The file can be limited to a range of file types and a maximum size.', 'Y', 'N', 'N', '2005-12-18 14:50:32', 'AJM', NULL, NULL);
REPLACE INTO `mnu_pattern` VALUES('LINK1', 'LINK1 - Update LINK table', 'This deals with a ONE-to-MANY-to-ONE relationship where MANY indicates a yes/no link between both of the ONE tables.\r\n\r\nThis allows multiple occurrences of MANY to be created, modified or deleted in a single screen.\r\n\r\nThis will call the ''updateLinkData'' method.', 'Y', 'Y', 'N', '2003-01-01 12:00:00', 'AJM', NULL, NULL);
REPLACE INTO `mnu_pattern` VALUES('LIST1', 'LIST1 - List multiple records', 'This will list all available rows from a database table one page at a time with (usually) 10 rows in each page.\r\n\r\nLinks at the bottom of the page will allow the user to jump to the first/last/previous/next page.\r\n\r\nThe selection may be refined by using the SEARCH button. This will bring up another screen into which selection criteria can be entered.', 'Y', 'N', 'Y', '2003-01-01 12:00:00', 'AJM', '2004-04-11 16:57:00', 'AJM');
REPLACE INTO `mnu_pattern` VALUES('LIST2', 'LIST2 - List multiple children', 'This will list rows of MANY in a ONE-to-MANY relationship one page at a time with (usually) 10 rows in each page.\r\n\r\nThe primary key of ONE will be passed down from the previous screen.\r\n\r\nLinks at the bottom of the page will allow the user to jump to the first/last/previous/next page.', 'Y', 'Y', 'N', '2003-01-01 12:00:00', 'AJM', '2007-03-20 18:00:48', 'AJM');
REPLACE INTO `mnu_pattern` VALUES('LIST3', 'LIST3 - List multiple children', 'This will list rows of MANY(1) and MANY(2) in a ONE-to-MANY(1)-to-MANY(2) relationship.\r\n\r\nMANY(1)-to-MANY(2) is another ONE-to-MANY relationship\r\n\r\nThe primary key of ONE will be passed down from the previous screen.\r\n\r\nThis will then be passed to MANY(1) for processing.\r\n\r\nThe primary key of MANY(1) will then be passed to MANY(2) for processing.\r\n\r\nLinks at the bottom of the page will allow the user to jump to the first/last/previous/next page.', 'Y', 'Y', 'N', '2005-06-22 23:17:59', 'AJM', '2007-04-09 13:47:05', 'AJM');
REPLACE INTO `mnu_pattern` VALUES('MENU', 'MENU - Display menu options', 'This will display a list of options and allow the user to select one.', 'Y', 'N', 'N', '2003-01-01 12:00:00', 'AJM', '2004-04-11 16:57:21', 'AJM');
REPLACE INTO `mnu_pattern` VALUES('MULTI1', 'MULTI1 - Update/Insert/delete multiple records', 'This will take a selection of primary keys and allow individual databases rows to be inserted, updated or deleted.\r\n\r\nThe identity of the row(s) to be processed is passed down from the previous screen, and scrolling between rows is controlled by the setScrollArray() and getScrollItem() methods\r\n\r\nThis will deal with one row at a time and will call either the ''insertRecord'', ''updateRecord'' or ''deleteRecord'' methods as appropriate.', 'Y', 'Y', 'N', '2003-01-01 12:00:00', 'AJM', '2005-06-24 23:20:22', 'AJM');
REPLACE INTO `mnu_pattern` VALUES('MULTI2', 'MULTI2 - Modify multiple records in a 2-level hierarchy', 'This will allow multiple MANY occurrences in a ONE-to-MANY relationship to be modified in a single operation. \r\n\r\nThe primary key of ONE is passed down from the previous screen.\r\n\r\nThis will call the ''updateMultiple'' method.', 'Y', 'Y', 'N', '2003-01-01 12:00:00', 'AJM', '2005-06-24 23:31:18', 'AJM');
REPLACE INTO `mnu_pattern` VALUES('MULTI3', 'MULTI3 - Modify multiple records in a 3-level hierarchy', 'This will allow updates to multiple occurrences of the CHILD table in a GRANDPARENT-PARENT-CHILD relationship n a single operation. \r\n\r\nThe primary key of GRANDPARENT is passed down from the previous screen.\r\n\r\nOccurrences of PARENT will start at the first available, with a scrolling area providing hyperlinks/buttons to step forwards or backwards through the available occurrences.\r\n\r\nOccurrences of CHILD will start at the first available page, with a pagination area providing hyperlinks/buttons to step forwards or backwards through the available occurrences.\r\n\r\nThis will call the ''updateMultiple'' method for the CHILD entity.', 'Y', 'Y', 'N', '2003-01-01 12:00:00', 'AJM', '2005-06-24 23:31:30', 'AJM');
REPLACE INTO `mnu_pattern` VALUES('MULTI4', 'MULTI4 - Update both in a ONE-to-MANY relationship', 'This will allow multiple MANY occurrences in a ONE-to-MANY relationship to be modified in a single operation. \r\n\r\nThe primary key of ONE is passed down from the previous screen.\r\n\r\nThis will call the ''updateMultiple'' method.', 'Y', 'Y', 'N', '2005-06-24 23:25:47', 'AJM', '2006-10-07 19:01:30', 'AJM');
REPLACE INTO `mnu_pattern` VALUES('OUTPUT1', 'OUTPUT1 - Output records to a CSV file', 'This will extract all available rows from a database table and output them to a CSV file which will be downloaded to the client device.\r\n\r\nIt will use any search criteria that is passed down from the calling screen.', 'N', 'N', 'N', '2006-08-11 16:49:59', 'AJM', '2006-08-11 17:01:35', 'AJM');
REPLACE INTO `mnu_pattern` VALUES('OUTPUT2', 'OUTPUT2 - Output records to a PDF file (List view)', 'This will extract all available rows from a database table and output them to a PDF file which will be downloaded to the client device.\r\n\r\nIt will use any search criteria that is passed down from the calling screen.\r\n\r\nList view produces column headings on the top of the page, with each database record taking up one line across the page.', 'N', 'N', 'N', '2006-08-13 11:11:04', 'AJM', '2006-08-16 22:29:20', 'AJM');
REPLACE INTO `mnu_pattern` VALUES('OUTPUT3', 'OUTPUT3 - Output records to a PDF file (Detail view)', 'This will extract all available rows from a database table and output them to a PDF file which will be downloaded to the client device.\r\n\r\nIt will use any search criteria that is passed down from the calling screen.\r\n\r\nDetail view produces one page per database record, one line per field, with labels on the left and values on the right.', 'N', 'N', 'N', '2006-08-14 13:30:18', 'AJM', NULL, NULL);
REPLACE INTO `mnu_pattern` VALUES('POPUP1', 'POPUP1 - Choose records (uses LIST1)', 'This shows the user a list of available rows from a database table and allows the user to choose one (or more) for processing in the previous screen.\r\n\r\nThis uses the LIST1 pattern.', 'Y', 'N', 'Y', '2006-10-06 13:07:29', 'AJM', '2006-10-06 13:12:02', 'AJM');
REPLACE INTO `mnu_pattern` VALUES('POPUP2', 'POPUP2 - Choose records (uses LIST2)', 'This shows the user a list of available rows from a database table and allows the user to choose one (or more) for processing in the previous screen.\r\n\r\nThis uses the LIST2 pattern.', 'Y', 'N', 'Y', '2006-10-06 13:08:16', 'AJM', '2006-10-06 13:12:05', 'AJM');
REPLACE INTO `mnu_pattern` VALUES('POPUP3', 'POPUP3 - Choose records (uses TREE1)', 'This shows the user a list of available rows from a database table and allows the user to choose one (or more) for processing in the previous screen.\r\n\r\nThis uses the TREE1 pattern.', 'Y', 'N', 'Y', '2006-10-06 13:10:16', 'AJM', '2006-10-06 13:12:08', 'AJM');
REPLACE INTO `mnu_pattern` VALUES('POPUP4', 'POPUP4 - Choose records (uses TREE2)', 'This shows the user a list of available rows from a database table and allows the user to choose one (or more) for processing in the previous screen.\r\n\r\nThis uses the TREE2 pattern.', 'Y', 'N', 'Y', '2006-10-06 13:10:45', 'AJM', '2006-10-06 13:12:11', 'AJM');
REPLACE INTO `mnu_pattern` VALUES('SRCH', 'SRCH - Enter selection criteria', 'This will show a blank detail screen and allow the user to specify search criteria that will then be returned to the previous screen for action.', 'Y', 'N', 'N', '2003-01-01 12:00:00', 'AJM', '2005-11-27 17:05:50', 'AJM');
REPLACE INTO `mnu_pattern` VALUES('TIMETABLE1', 'TIMETABLE1 - List timetable for a selected entity', 'This will list rows of TIMETABLE/SCHEDUILE table for a selected entity. It will show the schedule for every day in the week.\r\n\r\nThe primary key of selected entity will be passed down from the previous screen.\r\n\r\nLinks at the bottom of the page will allow the user to jump to the first/last/previous/next page.', 'Y', 'Y', 'N', '2006-07-10 21:35:00', 'AJM', NULL, NULL);
REPLACE INTO `mnu_pattern` VALUES('TREE1', 'TREE1 - List Tree Structure (fixed)', 'This will list nodes in a tree structure and allow the user to expand and contract nodes individually.\r\n\r\nThis uses a stand-alone TREE_NODE table.\r\n\r\nThis uses the ''getNodeData'' method to retrieve the data for the selected nodes.', 'Y', 'Y', 'Y', '2003-01-01 12:00:00', 'AJM', '2005-12-26 12:29:52', 'AJM');
REPLACE INTO `mnu_pattern` VALUES('TREE2', 'TREE2 - List Tree Structure (flexible)', 'This will list nodes in a tree structure and allow the user to expand and contract nodes individually.\r\n\r\nThis uses the TREE_TYPE, TREE_LEVEL and TREE_NODE database tables so that different levels can be defined for different tree types.\r\n\r\nThe TREE_TYPE to be displayed will be passed down by the previous screen.\r\n\r\nThis uses the ''getNodeData'' method to retrieve the data for the selected nodes.', 'Y', 'Y', 'Y', '2003-01-01 12:00:00', 'AJM', '2006-10-30 15:44:55', 'AJM');
REPLACE INTO `mnu_pattern` VALUES('UPD1', 'UPD1 - Update a single record', 'This will allow a single database record (row) to be displayed and updated.\r\n\r\nThe primary key of the row is passed down from the previous screen. If multiple rows are selected then scrolling options will be provided.\r\n\r\nThis will call the ''updateRecord'' method.', 'Y', 'Y', 'N', '2003-01-01 12:00:00', 'AJM', '2004-04-11 16:43:21', 'AJM');
REPLACE INTO `mnu_pattern` VALUES('UPD2', 'UPD2 - Update multiple records', 'This will update multiple rows in the database.\r\n\r\nThe rows are identified using a combination of values supplied by the previous screen plus values selected in a popup screen which will be called next.\r\n\r\nThis will call the ''updateSelection'' method.', 'N', 'Y', 'N', '2003-01-01 12:00:00', 'AJM', '2007-04-15 15:18:45', 'AJM');
REPLACE INTO `mnu_pattern` VALUES('UPD3', 'UPD3 - Update Control record', 'This will update multiple rows in a ''control'' table in the database.\r\n\r\nEach row contains a ''field_id'' and a ''field_value'' column, and they are treated as searate fields from a sigle row. This design allows ''fields'' (rows) to be added or deleted at will without having to alter the structure of the table.\r\n\r\nThis will call the ''updateSelection'' method.', 'Y', 'N', 'N', '2003-01-01 12:00:00', 'AJM', '2004-05-28 11:56:43', 'AJM');
REPLACE INTO `mnu_pattern` VALUES('UPD4', 'UPD4 - Update one or more records without user dialog', 'This will allow one or more database rows to be updated without being displayed.\r\n\r\nThe identity of the row(s) to be updated is passed down from the previous screen.\r\n\r\nThis will call the ''updateMultiple'' method.', 'N', 'Y', 'N', '2003-01-01 12:00:00', 'AJM', '2004-05-28 12:37:18', 'AJM');
REPLACE INTO `mnu_pattern` VALUES('OTHER', 'Other - Miscellaneous - Undefined', 'This is for anything whch does not fall into any recognisable category.', 'N', 'N', 'N', '2007-01-10 18:21:02', 'AJM', NULL, NULL);
REPLACE INTO `mnu_pattern` VALUES('UPD5', 'UPD5 - Insert/Update one or more records without user dialog', 'This will allow one or more database rows to be inserted or updated without being displayed.\r\n\r\nThe identity of the row(s) to be processed is passed down from the previous screen so that they can be assembled.\r\n\r\nThis will call the ''insertOrUpdate'' method which will check if each record currently exists, then insert or update as appropriate.', 'N', 'Y', 'N', '2007-04-15 15:33:17', 'AJM', NULL, NULL);
REPLACE INTO `mnu_pattern` VALUES('ADD5', 'ADD5 - Insert mutiple records', 'This will allow several database records to be created from a single input screen.\r\n\r\nDetails of the parent table are passed down in $where.\r\n\r\nThis will use the ''insertMultiple'' method.', 'Y', 'Y', 'N', '2008-03-28 19:00:53', 'AJM', NULL, NULL);
REPLACE INTO `mnu_pattern` VALUES('OUTPUT4', 'OUTPUT4 - Output selected fields to a CSV file', 'This will extract all available rows from a database table and output selected fields to a CSV file which will be downloaded to the client device.\r\n\r\nThe user is first shown a list of available fields and asked to select which ones should be included in the output.\r\n\r\nIt will use any search criteria that is passed down from the calling screen.', 'Y', 'N', 'N', '2008-06-15 11:49:56', 'AJM', NULL, NULL);
REPLACE INTO `mnu_pattern` VALUES('BATCH', 'BATCH - Background process', 'This process will be run in the background as it may require to run longer than the limit available for web pages. The output will be stored in the LOGS directory.', 'N', 'N', 'N', '2008-11-24 11:39:28', 'AJM', NULL, NULL);
